import * as date from '../../src/common/date';

describe.skip('test src/common/date.ts', () => {
    it('test formatDate ddd mmm dd yyyy HH:M:s', () => {
        const res = date.formatDate(1669279479819, "ddd mmm dd yyyy HH:M:s");
        expect(res).toBe('Thu Nov 24 2022 16:44:39');
    });
    it('test formatDate yyyy年mm月dd日 hh时MM分ss秒', () => {
        const res = date.formatDate(1669279479819, "yyyy年mm月dd日 hh时MM分ss秒");
        expect(res).toBe('2022年11月24日 04时44分39秒');
    });
    it('test formatDate yy年mm月dd日 H时MM分ss秒', () => {
        const res = date.formatDate(
            1622172155227,
            "yy年mm月dd日 H时MM分ss秒",
            false,
            false
        );
        expect(res).toBe('21年05月28日 11时22分35秒');
    });
    it('test formatDate yy年mm月d日 HH时MM分ss秒', () => {
        const res = date.formatDate(
            1622172155227,
            "yy年mm月d日 HH时MM分ss秒",
            true
        );
        expect(res).toBe('21年05月28日 03时22分35秒');
    });
    it('test formatDate yy年m月DDD日 HH时MM分ss秒', () => {
        const res = date.formatDate(
            1622172155227,
            "yy年m月DDD日 HH时MM分ss秒",
            true
        );
        expect(res).toBe('21年5月Fri日 03时22分35秒');
    });
    it('test formatDate yy年mm月dddd日 HH时MM分ss秒', () => {
        const res = date.formatDate(
            1622172155227,
            "yy年mm月dddd日 HH时MM分ss秒",
            true
        );
        expect(res).toBe('21年05月Friday日 03时22分35秒');
    });
    it('test formatDate yy年mm月DDDD日 HH时MM分ss秒', () => {
        const res = date.formatDate(
            1622172155227,
            "yy年mm月DDDD日 HH时MM分ss秒",
            true
        );
        expect(res).toBe('21年05月Friday日 03时22分35秒');
    });
    it('test formatDate yy年mmmm月DDDD日 h时MM分ss秒', () => {
        const res = date.formatDate(
            1622172155227,
            "yy年mmmm月DDDD日 h时MM分ss秒",
            true
        );
        expect(res).toBe('21年May月Friday日 3时22分35秒');
    });
    it('test formatDate h:MM:ss t l L Z tt TT o p S W WW N', () => {
        const res = date.formatDate(
            1622172155227,
            "h:MM:ss t l L Z tt TT o p S W WW N",
            true
        );
        expect(res).toBe('3:22:35 a 227 22 UTC am AM +0000 +00:00 th 21 21 5');
    });
    
    it('test formatDate arguments.length=1', () => {
        try {
            const res = date.formatDate(
                'ffff'
            );
            expect(res).toBe('21年05月28日 03时22分35秒');
        } catch(err) {}
    });
    it('test formatDate UTC:yyyy-mm-dd', () => {
        const res = date.formatDate(
            1622172155227,
            "UTC:yyyy-mm-dd'T'HH:MM:ss'Z'",
            true
        );
        expect(res).toBe('UAC:2021-05-28T03:22:35Z');
    });
});