import { If, IsEmpty, NotNull, And, Or } from '../../../src/common/logic/index';
// 条件分支
test('test If function', async () => {
  expect(If(!!'', '11', '22')).toEqual('22');
  expect(If(!'', '11', '22')).toEqual('11');
  expect(If(!null, '11', '22')).toEqual('11');
  expect(If(!!null, '11', '22')).toEqual('22');
});
// 是否为空文本
test('test IsEmpty function', async () => {
  expect(IsEmpty('11')).toEqual(false);
  expect(IsEmpty('')).toEqual(true);
  expect(IsEmpty([])).toEqual(true);
  expect(IsEmpty(['1', ''])).toEqual(false);
  expect(IsEmpty(['', ''])).toEqual(true);
});
// 是否包含数据
test('test NotNull function', async () => {
  expect(NotNull(null)).toEqual(true);
  expect(NotNull('')).toEqual(false);
  expect(NotNull(undefined)).toEqual(false);
  expect(NotNull(new Date('帅哥'))).toEqual(false);
  expect(NotNull(0)).toEqual(false);
});
// 与
test('test And function', async () => {
  expect(And(!null, !!'11', !!'22')).toEqual(true);
  expect(And(!!null, !!'11', !!'22')).toEqual(false);
  expect(And(true, true, true)).toEqual(true);
  expect(And(true, true, false)).toEqual(false);
});
// 或
test('test Or function', async () => {
  expect(Or(!null, !!'11', !!'22')).toEqual(true);
  expect(Or(!!null, !!'11', !!'22')).toEqual(true);
  expect(Or(true, true, true)).toEqual(true);
  expect(Or(true, true, false)).toEqual(true);
  expect(Or(false, false, false)).toEqual(false);
});
