const { isString, isNumber, isDate } = require('lodash-es');

// 自定义匹配器
/* istanbul ignore next */
expect.extend({
  isString(val) {
    if (isString(val)) {
      return {
        message: () => `是string类型`,
        pass: true,
      };
    } else {
      return {
        message: () => `不是string类型`,
        pass: false,
      };
    }
  },
});
/* istanbul ignore next */
expect.extend({
  isDate(val) {
    if (isDate(val)) {
      return {
        message: () => `是Date类型`,
        pass: true,
      };
    } else {
      return {
        message: () => `不是Date类型`,
        pass: false,
      };
    }
  },
});
/* istanbul ignore next */
expect.extend({
  isNumber(val) {
    if (isNumber(val)) {
      return {
        message: () => `是number类型`,
        pass: true,
      };
    } else {
      return {
        message: () => `不是number类型`,
        pass: false,
      };
    }
  },
});

