describe('test src/web/auth/user.ts getUserInfo', () => {
  it('test getUserInfo', async () => {
    jest
      .spyOn(WEDA_CLOUD_SDK, 'initTcb')
      .mockReturnValue(new Promise((resolve) => resolve({ app: null as any, auth: null as any })));
    jest
      .spyOn(WEDA_CLOUD_SDK.CLOUD_SDK, 'getUserInfo')
      .mockReturnValue(new Promise((resolve) => resolve(userInfoFromSDK)));
    jest.spyOn(user, 'getUserInfoFromCloudSDK').mockReturnValue(new Promise((resolve) => resolve(userInfo)));
    const res = await user.getUserInfo();
    expect(JSON.parse(JSON.stringify(res))).toStrictEqual(userInfo);
    const currentUser = user.userObj.currentUser;
    expect(JSON.parse(JSON.stringify(currentUser))).toStrictEqual(userInfo);
  });
});

describe('test src/web/auth/user.ts signIn', () => {
  it('test signIn', async () => {
    const result = {
      Data: [],
    };
    jest.spyOn(service, 'callWedaApiFn').mockImplementationOnce(async () => {
      return new Promise((resolve, reject) => {
        return resolve(result);
      });
    });
    jest.spyOn(WEDA_CLOUD_SDK, 'getTcbInstance').mockImplementationOnce(
      async () =>
        ({
          auth: {
            signIn: jest.fn(),
            getLoginState: () => ({}),
          },
        } as any),
    );
    jest
      .spyOn(WEDA_CLOUD_SDK.CLOUD_SDK, 'getUserInfo')
      .mockReturnValue(new Promise((resolve) => resolve(userInfoFromSDK)));
    Object.defineProperty(window, '_WedaHostConfig', {
      value: {
        login: jest.fn(),
      },
      writable: true,
    });
    await user.signIn({ username: 'xxx', password: 'xxx' });
    expect(WEDA_CLOUD_SDK.getTcbInstance).toBeCalled();
    expect(window._WedaHostConfig.login).toBeCalled();
  });
});

