describe('test src/web/auth/user.ts filterCloudSdkUserInfo', () => {
  it('test filterCloudSdkUserInfo wedaUser.userType=anonymousUser', async () => {
    const userInfoRes = {
      userType: 'anonymousUser',
      type: 1,
      openId: userInfo.openId,
      wedaId: userInfoFromSDK.wedaId,
    };
    const res = user.filterCloudSdkUserInfo(userInfoRes);
    expect(res).toMatchObject({ type: 1 });
  });
  it('test filterCloudSdkUserInfo wedaUser={}', async () => {
    const res = user.filterCloudSdkUserInfo({});
    expect(res).toMatchObject({ type: undefined });
  });
});

