export declare const EVENT_BUS: {
    /**
     * 监听事件
     * @param eventName 事件名称
     * @param callback 回调函数
     */
    on(eventName: string, callback: Function): void;
    once(eventName: string, callback: Function): number | undefined;
    /**
     * 触发事件
     * @param eventName 事件名称
     * @param payload 事件参数
     * @return 返回事件回调的结果, 需使用 promise 方式获取
     */
    emit(eventName: string, ...params: unknown[]): Promise<void>;
    /**
     * 移除事件, 支持批量移除
     * @param eventName 事件名称
     **/
    off(eventName: string | string[]): void;
    /**
     * 监听广播事件
     * @param eventName 事件名称
     * @param callback 回调函数
     */
    listen(eventName: string, callback: Function): void;
    /**
     * 广播事件
     */
    broadcast(eventName: string, ...args: any[]): void;
    /**
     * 不再监听广播事件
     * @param eventName 事件名称
     * @param callback 回调函数
     */
    quit(eventName: string, callback: Function): void;
};
