export interface IUserInfo {
    /** 用户在weda 侧的ID */
    wedaId: string;
    /** 昵称/用户名 */
    nickName: string;
    /** 头像地址 */
    avatarUrl: string;
    /** 国家 */
    country?: string;
    /** 省份 */
    province?: string;
    /** 城市 */
    city?: string;
    /** 0: 未知, 1: 男, 2: 女 */
    gender: number;
    /** 小程序用户信息的最后获取时间, 仅在小程序中有用 */
    lastUpdateTime?: number;
    /** 微信openId */
    wxOpenId?: string;
}
/**
 * 门户中 _WedaHostConfig 挂载的 wedaUser 信息结构
 *  该数据责任人 celesteliao(廖泽宇)
 */
export interface IWedaHostUser {
    accessibleService?: '*' | string[];
    relatedRoles?: any;
    avatar: string;
    uin?: string;
    type?: number;
    email?: string;
    name?: string;
    nickName?: string;
    envId?: string;
    userId?: string;
    uuid?: string;
    phone?: string;
    userExtend?: string;
}
export interface ILoginState {
    /** 匿名用户 */
    isAnonymous: boolean;
    /** 未登录 */
    notLogin: boolean;
    /** 已具名登录 */
    hasLogin: boolean;
    /** 用户scope group信息 */
    groups?: string[];
    /** 登录 jssdk 版本 */
    version?: string;
}
