export interface IWrapperDatasourceOptions {
    /** 数据源名称 */
    dataSourceName: string;
    /** 底层查询使用的数据源方法 */
    methodName: string;
}
/**
 * cloud 工具函数列表
 */
export declare const utils: {
    /**
     * 包装数据源方法, 固定请求时的 dataSourceName 和 methodName, 返回一个新方法, 新方法的入参即为数据源方法的入参
     */
    wrapperDatasourceMethod(options: IWrapperDatasourceOptions): (params: any) => Promise<any>;
};
