/**
 * 工具API
 */
import { callDataSource } from './basic-api';
/**
 * cloud 工具函数列表
 */
export const utils = {
    /**
     * 包装数据源方法, 固定请求时的 dataSourceName 和 methodName, 返回一个新方法, 新方法的入参即为数据源方法的入参
     */
    wrapperDatasourceMethod(options) {
        return function (params) {
            return callDataSource({
                name: options.dataSourceName,
                methodName: options.methodName,
                params,
            });
        };
    },
};
